<?php//if ($_SERVER["REQUEST_METHOD"] == "POST") {error_reporting(1); if (isset($_POST['contact1fs'])) {        if (isset($_POST['name']) && !empty($_POST['name']) AND isset($_POST['empresa']) && !empty($_POST['empresa']) AND isset($_POST['telefono']) && !empty($_POST['telefono']) AND isset($_POST['email']) && !empty($_POST['email']) AND isset($_POST['pais']) && !empty($_POST['pais']) AND isset($_POST['especificacion']) && !empty($_POST['especificacion']) AND isset($_POST['message']) && !empty($_POST['message'])) {            // Todo esta fino            $name = stripslashes($_POST['name']);            $company = stripslashes($_POST['empresa']);            $phone = stripslashes($_POST['telefono']);            $email = stripslashes($_POST['email']);            $country = stripslashes($_POST['pais']);            $specification = stripslashes($_POST['especificacion']);            $message = stripslashes($_POST['message']);            $datos = $name.';'.$company.';'.$phone.';'.$email.';'.$country.';'.$specification.';'.$message;            enviarMensaje($datos, "2");            } else {                header("Location: error.php?e=0"); //falto datos            }    } /* } else {    header("Location: index.php?e=3"); // se accedió al archivo directamente }*/function enviarMensaje($datos, $paso){    $nombre = $apellido = $mail = $mensaje = "";    $to = "alugox@gmail.com";    $subject = "fs Magazine - Nuevo Mensaje de Contacto";    $htmlContent1 = file_get_contents("componentes/correos/bienvenido1.html");    if($paso == "1"){        list($nombre, $apellido, $mail, $mensaje) = explode(";", $datos);        $htmlContent2 = "<span class='header-sm'>Nombre y Apellido:</span> <b>$nombre $apellido</b><br />        <span class='header-sm'>Correo:</span> <b>$mail</b><br />        <span class='header-sm'>Mensaje:</span> <b>$mensaje</b><br /><br />";    } else if($paso == "2"){        list($nombre, $company, $phone, $mail, $pais, $requerido, $mensaje) = explode(";", $datos);        $htmlContent2 = "<span class='header-sm'>Nombre:</span> <b>$nombre</b><br />        <span class='header-sm'>Correo:</span> <b>$mail</b><br />        <span class='header-sm'>Teléfono:</span> <b>$phone</b><br />        <span class='header-sm'>Compañia:</span> <b>$company</b><br />        <span class='header-sm'>País:</span> <b>$pais</b><br />        <span class='header-sm'>Requerimiento:</span> <b>$requerido</b><br />        <span class='header-sm'>Mensaje:</span> <b>$mensaje</b><br /><br />";    }    $htmlContent3 = file_get_contents("componentes/correos/bienvenido2.html");    $REMOTE_USER = (isset($_SERVER["REMOTE_USER"]))?$_SERVER["REMOTE_USER"]:"";    $REMOTE_ADDR = (isset($_SERVER["REMOTE_ADDR"]))?$_SERVER["REMOTE_ADDR"]:"";    $Message .= "REMOTE USER: ". $REMOTE_USER."\n";    $Message .= "REMOTE ADDR: ". $REMOTE_ADDR."\n";    $htmlContent = $htmlContent1.$htmlContent2.$htmlContent3."<br>".$Message;    // Set content-type header for sending HTML email    $headers = "MIME-Version: 1.0" . "\r\n";    $headers .= "Content-type:text/html;charset=UTF-8" . "\r\n";    // Additional headers    $headers .= 'From: Fs Magazine <marketing@fsinter.com>' . "\r\n";    //$headers .= 'Cc: ' . "\r\n";   //$headers .= 'Bcc: ' . "\r\n";    // Send email    if(mail($to,$subject,$htmlContent,$headers)){        return "1";        //header("Location: gracias.php");    } else{        return "0";          //header("Location: error.php?e=1");     }}?>