<?phpfunction ConsultaCC($numero) {    require_once "../../sec/libcon.php";    $dbh = dbconncc();    mysqli_set_charset($dbh, 'utf8');    if (!$dbh) {        die('Error en Conexión: ' . mysqli_error($dbh));        exit;    }        $esta = array(1 => "Activo", 0 => "No activo", 2 => "Desactivado");    $veri = array(0 => "No", 1 => "Si", 2 => "Desactivada");    $sql = "SELECT * FROM CLIENTE where CLIENTCARD = $numero";    $search = mysqli_query($dbh, $sql) or die(mysqli_error($dbh));    $match = mysqli_num_rows($search);    if ($match > 0) {        while ($rw = mysqli_fetch_array($search)) {            $cod  = $rw['CODIGO'];            $nombre  = $rw['NOMBRE'];            $direccion  = $rw['DIRECCION'];            $telefono  = $rw['TELEFONO'];            $fechacreado  = $rw['FECHACREADO'];            $correover  = $rw['CORREOVERIFICADO'];            $correo  = $rw['CORREO'];            $password  = $rw['PASSWORD'];            $estado  = $rw['ESTATUS'];            $codigosalon  = $rw['CODIGOSALON'];        }                echo "            <div class='form-group'>                <label class='control-label col-sm-7' for='titulo'>Datos del Cliente:</label>            </div>            <div class='form-group'>                <label class='control-label col-sm-9' for='titulo'>Estado: ".$esta[$estado]." | Cuenta Activada: ".$veri[$correover]."</label>            </div>            <div class='form-group'>                <label class='control-label col-sm-4' for='t1'>Núm. del Cliente:</label>                <div class='col-sm-2'><input type='text' class='form-control' id='t1' name='t1' value='$cod' /></div>            </div>            <div class='form-group'>                <label class='control-label col-sm-4' for='t2'>Nombre:</label>                <div class='col-sm-6'><input type='text' class='form-control' id='t2' name='t2' value='$nombre' /></div>            </div>                <div class='form-group'>                <label class='control-label col-sm-4' for='t3'>Dirección:</label>                <div class='col-sm-6'><input type='text' class='form-control' id='t3' name='t3' value='$direccion' /></div>            </div>                <div class='form-group'>                <label class='control-label col-sm-4' for='t4'>Teléfono:</label>                <div class='col-sm-6'><input type='text' class='form-control' id='t4' name='t4' value='$telefono' /></div>            </div>                <div class='form-group'>                <label class='control-label col-sm-4' for='t5'>Fecha de Creación:</label>                <div class='col-sm-6'><input type='text' class='form-control' id='t5' name='t5' value='$fechacreado' /></div>            </div>                                              <div class='form-group'>                <label class='control-label col-sm-4' for='t7'>Correo:</label>                <div class='col-sm-6'><input type='text' class='form-control' id='t7' name='t7' value='$correo' /></div>            </div>              <div class='form-group'>                <label class='control-label col-sm-4' for='t8'>Contraseña (hash):</label>                <div class='col-sm-6'><input type='text' class='form-control' id='t8' name='t8' value='$password' /></div>            </div>               <div class='form-group'>                <label class='control-label col-sm-4' for='t10'>Cód. en Salón:</label>                <div class='col-sm-6'><input type='text' class='form-control' id='t10' name='t10' value='$codigosalon' /></div>            </div>";    } else{        echo "<div class='form-group'>                <label class='control-label col-sm-7' for='titulo'>Cliente no encontrado.</label>            </div>";    }}function VerificarCC($id){    require_once "../../sec/libcon.php";    $dbh = dbconncc();    mysqli_set_charset($dbh, 'utf8');    if (!$dbh) {        die('Error en Conexión: ' . mysqli_error($dbh));        exit;    }    $sql = "SELECT * FROM CLIENTCARD where CODIGO = $id";    $search = mysqli_query($dbh, $sql) or die(mysqli_error($dbh));    $match = mysqli_num_rows($search);    if ($match > 0) {        echo "<div class='form-group'>                <label class='control-label col-sm-7' for='titulo'>Código de ClientCard existe.</label>            </div>";    } else {        echo "<div class='form-group'>                <label class='control-label col-sm-7' for='titulo'>Código de ClientCard no fue encontrado.</label>            </div>";    }}function listarSelectSalones(){    require_once "../../sec/libcon.php";    $dbh = dbconncc();    mysqli_set_charset($dbh, 'utf8');    if (!$dbh) {        die('Error en Conexión: ' . mysqli_error($dbh));        exit;    }        $sql = "SELECT * FROM SALONES";    $search = mysqli_query($dbh, $sql) or die(mysqli_error($dbh));    $match = mysqli_num_rows($search);    if ($match > 0) {        while ($rw = mysqli_fetch_array($search)) {            $cod  = $rw['CODIGO'];            $nombre  = $rw['NOMBRE'];            echo "<option value='$cod'>$nombre</option>";        }    }     }function insertbono($numero, $salon){    error_reporting(1);    require_once "../../sec/libcon.php";    $dbh = dbconn();    mysqli_set_charset($dbh, 'utf8');    if (!$dbh) {        die('Error en Conexión: ' . mysqli_error($dbh));        exit;    }    $fecha = date("Y-m-d");    /*$sql = "INSERT INTO MOVIMIENTOSPUNTOS (FECHA, HORA, TIPO, ORIGEN, DESCRIPCION, MONTOTRANSACCION, PUNTOSAFECTADOS, CODIGOCLIENTE, DOCUMENTOORIGEN) VALUES ('$fecha', '06:00:00', 'I', '$salon', '&#161;Bonificaci&#243;n por Verificar tu Correo!', '0', '3000', $numero, 'BONO0001' )";    if (mysqli_query($dbh, $sql)) {        return "1";    } else {        return "0";    }*/    echo $numero;}function mostrarPass($numero) {    $codigo = base64_decode($numero);    return $codigo;}function mostrarMail($mail) {    require_once "../../sec/libcon.php";    $dbh = dbconncc();    mysqli_set_charset($dbh, 'utf8');    if (!$dbh) {        die('Error en Conexión: ' . mysqli_error($dbh));        exit;    }    $esta = array(1 => "Activo", 0 => "No activo", 2 => "Desactivado");    $veri = array(0 => "No", 1 => "Si", 2 => "Desactivada");    $sql = "SELECT * FROM CLIENTE where CORREO = '$mail' LIMIT 1";    $search = mysqli_query($dbh, $sql) or die(mysqli_error($dbh));    $match = mysqli_num_rows($search);    if ($match > 0) {        while ($rw = mysqli_fetch_array($search)) {            $cod  = $rw['CODIGO'];            $nombre  = $rw['NOMBRE'];            $direccion  = $rw['DIRECCION'];            $telefono  = $rw['TELEFONO'];            $fechacreado  = $rw['FECHACREADO'];            $correover  = $rw['CORREOVERIFICADO'];            $clientcard  = $rw['CLIENTCARD'];            $password  = $rw['PASSWORD'];            $estado  = $rw['ESTATUS'];            $codigosalon  = $rw['CODIGOSALON'];        }        echo "            <div class='form-group'>                <label class='control-label col-sm-7' for='titulo'>Datos del Cliente:</label>            </div>            <div class='form-group'>                <label class='control-label col-sm-9' for='titulo'>Estado: ".$esta[$estado]." | Cuenta Activada: ".$veri[$correover]."</label>            </div>            <div class='form-group'>                <label class='control-label col-sm-4' for='t1'>Núm. del Cliente:</label>                <div class='col-sm-2'><input type='text' class='form-control' id='t1' name='t1' value='$cod' /></div>            </div>            <div class='form-group'>                <label class='control-label col-sm-4' for='t2'>Nombre:</label>                <div class='col-sm-6'><input type='text' class='form-control' id='t2' name='t2' value='$nombre' /></div>            </div>                <div class='form-group'>                <label class='control-label col-sm-4' for='t3'>Dirección:</label>                <div class='col-sm-6'><input type='text' class='form-control' id='t3' name='t3' value='$direccion' /></div>            </div>                <div class='form-group'>                <label class='control-label col-sm-4' for='t4'>Teléfono:</label>                <div class='col-sm-6'><input type='text' class='form-control' id='t4' name='t4' value='$telefono' /></div>            </div>                <div class='form-group'>                <label class='control-label col-sm-4' for='t5'>Fecha de Creación:</label>                <div class='col-sm-6'><input type='text' class='form-control' id='t5' name='t5' value='$fechacreado' /></div>            </div>                                              <div class='form-group'>                <label class='control-label col-sm-4' for='t7'>ClientCard:</label>                <div class='col-sm-6'><input type='text' class='form-control' id='t7' name='t7' value='$clientcard' /></div>            </div>              <div class='form-group'>                <label class='control-label col-sm-4' for='t8'>Contraseña (hash):</label>                <div class='col-sm-6'><input type='text' class='form-control' id='t8' name='t8' value='$password' /></div>            </div>               <div class='form-group'>                <label class='control-label col-sm-4' for='t10'>Cód. en Salón:</label>                <div class='col-sm-6'><input type='text' class='form-control' id='t10' name='t10' value='$codigosalon' /></div>            </div>";    } else{        echo "<div class='form-group'>                <label class='control-label col-sm-7' for='titulo'>Cliente no encontrado.</label>            </div>";    }}if (isset($_GET['a'])) {    $id = $_GET['a'];    ConsultaCC($id);} else if (isset($_GET['c'])) {    $id = $_GET['c'];    VerificarCC($id);} else if (isset($_GET['e'])){    $id = $_GET['e'];    echo mostrarPass($id);} else if (isset($_GET['d'])){    $id = $_GET['d'];    mostrarMail($id);}?>