 else {        die(mysqli_error($dbh));    }}if (isset($_GET['q'])) {    $id = $_GET['q'];    llenarInputDepartamentos($id);} else if (isset($_GET['w'])){    $id = $_GET['w'];    mostrarHijosCategoria($id);} else if (isset($_GET['ww'])){    $idcat = $_GET['ww'];    modNombreCategoria($idcat);} else if (isset($_GET['x'])){    $id = $_GET['x'];    mostrarHijosContenido($id);//    mostrarUpload($id);}  else if (isset($_GET['xx'])){    $id = $_GET['xx'];    mostrarUpload($id);} /*else if (isset($_GET['y'])){    $id = $_GET['y'];    $extension = $_GET['yy'];    $tipo = $_GET['y3'];    $user = $_GET['y4'];    $categoria = $_GET['y5'];    $nombre2 = $_GET['y6'];    addContenido($id, $extension, $tipo, $user, $categoria, $nombre2);}*/?>